DROP TABLE IF EXISTS `weapons`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!50503 SET character_set_client = utf8mb4 */;
CREATE TABLE `weapons` (
  `id` bigint NOT NULL AUTO_INCREMENT COMMENT '무기 PK',
  `slug` varchar(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '무기 고유 식별자(URL 및 API용)',
  `name` varchar(100) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '무기 이름',
  `image` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '무기 이미지 경로',
  `category` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '무기 카테고리 (melee/ranged)',
  `weapon_type` varchar(20) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '무기 타입 (sword, bow 등)',
  `attack_type` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci NOT NULL COMMENT '공격 타입 (slash/spike/smash)',
  `element` varchar(10) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '액티브 스킬 속성 (nullable)',
  `attack` decimal(8,2) NOT NULL COMMENT '기본 공격력',
  `crit_rate` decimal(5,2) NOT NULL COMMENT '치명타 확률(%)',
  `crit_damage` decimal(5,2) NOT NULL COMMENT '치명타 피해량(%)',
  `attack_speed` decimal(4,2) NOT NULL COMMENT '공격 속도',
  `trigger_probability` decimal(5,2) NOT NULL COMMENT '발동 확률(%)',
  `charge_attack_speed` decimal(4,2) DEFAULT NULL COMMENT '차지 공격 속도 (근접 전용)',
  `fall_attack_speed` decimal(4,2) DEFAULT NULL COMMENT '낙하 공격 속도 (근접 전용)',
  `multishot` int DEFAULT NULL COMMENT '다중 사격 (원거리 전용)',
  `max_ammo` int DEFAULT NULL COMMENT '최대 탄약 (원거리 전용)',
  `ammo_conversion_rate` decimal(5,2) DEFAULT NULL COMMENT '탄약 전환율 (원거리 전용)',
  `passive_stat` varchar(30) CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci DEFAULT NULL COMMENT '패시브 스탯 종류 (WORD 코드 참조)',
  `passive_value` decimal(6,2) DEFAULT NULL COMMENT '패시브 수치(%)',
  `active_skill_description` text CHARACTER SET utf8mb4 COLLATE utf8mb4_unicode_ci COMMENT '액티브 스킬 설명',
  `created_at` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '생성 일시',
  `mag_capacity` int DEFAULT NULL COMMENT '탄창 용량',
  PRIMARY KEY (`id`),
  UNIQUE KEY `uk_weapon_slug` (`slug`)
) ENGINE=InnoDB AUTO_INCREMENT=56 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci COMMENT='무기 정보';
